mapper.insert:
INSERT INTO mber
	(
		MBER_ID
		, MBER_PW
		, MBER_NCNM
		, MBER_EML
		, MBER_TEL
		, MBER_ADDR
		, MBER_ZIP
		, MBER_STTUS
		, MBER_SEXDSTN
		, ATCH_CODE
		, CNRS_CD
		, CRT_DT
		, MBER_LAST_CONECTDT
	)
VALUES
	(
		#{mberId}
		, #{mberPw}
		, #{mberNcnm}
		, #{mberEml}
		, #{mberTel}
		, #{mberAddr}
		, #{mberZip}
		, #{mberSttus}
		, #{mberSexdstn}
		, #{atchCode}
		, #{cnrsCd}
		, CURRENT_TIMESTAMP
		, CURRENT_TIMESTAMP
	)

mapper.update:
UPDATE mber
SET  
	REFRESH_TOKEN=#{refreshToken}
WHERE MBER_ID=#{mberId}

mapper.select:
SELECT M.REFRESH_TOKEN 
FROM mber M 
WHERE  MBER_ID =#{mberId}
